CREATE TABLE PERSON (ID BIGINT  PRIMARY KEY AUTO_INCREMENT,FIRST_NAME VARCHAR(255),LAST_NAME VARCHAR(255),ADDRESS VARCHAR(255),email VARCHAR(255),cpf VARCHAR(255),senha VARCHAR(255),celular VARCHAR(255),curriculoNome VARCHAR(255),profissao VARCHAR(255),experiencia VARCHAR(255),educacao VARCHAR(255),infoExtra VARCHAR(255),chaves VARCHAR(255),favorito VARCHAR(255));	
CREATE TABLE candidato (ID BIGINT  PRIMARY KEY AUTO_INCREMENT,nome VARCHAR(255),email VARCHAR(255),cpf VARCHAR(255),senha VARCHAR(255),celular VARCHAR(255));
CREATE TABLE recrutador (ID BIGINT  PRIMARY KEY AUTO_INCREMENT,nome VARCHAR(255),email VARCHAR(255),cnpj VARCHAR(255),empresa VARCHAR(255),senha VARCHAR(255),telefone VARCHAR(255));